sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;




  var slides = document.getElementsByClassName('carousel-item'),
      addActive = function(slide) {slide.classList.add('active')},
      removeActive = function(slide) {slide.classList.remove('active')};

  setInterval(function (){
    for (var i = 0; i < slides.length; i++){
      if (i + 1 == slides.length) {
        addActive(slides[0]);
        slides[0].style.zIndex = 100;
        setTimeout(removeActive, 350, slides[i]); //Doesn't be worked in IE-9
        break;
      }
      if (slides[i].classList.contains('active')) { 
        slides[i].removeAttribute('style');
        setTimeout(removeActive, 350, slides[i]); //Doesn't be worked in IE-9
        addActive(slides[i + 1]);
        break;
      }
    } 
  }, 4000);



}

sp_widget.css:
.icon-img{
  height: 100%;
  width:100%;

}


.carousel {
  position: relative;
  width: 100%;
  height: 500px;
  border-radius: 4px;
  overflow: hidden;
  a:hover, a:focus {
    box-shadow: none;
    img {
      -webkit-filter: unquote("brightness(50%)") ;
    }
  }
  .carousel-item {
    position: absolute;
		width: 100%;
  	height: 100%;
    top: 0;
    left: 100%;
     border-radius: 4px;
    &.active {
      left: 0;
      transition: all 0.3s ease-out;
    }

  }
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */


	try { 
		var r = new sn_ws.RESTMessageV2('x_1007530_med_news.MedicalImg', 'GetImg');

		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();

		var	rawImg = JSON.parse(responseBody);
		data.first = rawImg[0].urls.raw;
		data.fdescription = rawImg[0].alt_description;

		data.second = rawImg[1].urls.raw;
		data.sdescription = rawImg[1].alt_description;

		data.third = rawImg[2].urls.raw;
		data.tdescription = rawImg[2].alt_description;

	}
	catch(ex) {
		var message = ex.message;
	}

})();
